--//npgConexao = new NpgsqlConnection("Server=andretecnologia.com.br;Port=5432;User Id=postgres;Password=postgres;Database=imobiliaria;Timeout=60;");--//npgConexao = new NpgsqlConnection("Server=localhost;Port=5432;User Id=postgres;Password=postgres;Database=imobiliaria;Timeout=60;");            CREATE DATABASE imobiliaria    WITH     OWNER = postgres    ENCODING = 'UTF8'    LC_COLLATE = 'en_US.UTF-8'    LC_CTYPE = 'en_US.UTF-8'    TABLESPACE = pg_default    CONNECTION LIMIT = -1;			CREATE TABLE public.alugueis(    i_pagamentos integer NOT NULL,    i_imoveis integer NOT NULL,    i_pessoas integer NOT NULL,    i_alugueis integer NOT NULL,    CONSTRAINT i_alugueis PRIMARY KEY (i_pagamentos, i_imoveis, i_pessoas, i_alugueis),    CONSTRAINT pagamentos_alugueis_fk FOREIGN KEY (i_pagamentos)        REFERENCES public.pagamentos (i_pagamentos) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE NO ACTION        NOT VALID,    CONSTRAINT pessoas_alugueis_fk FOREIGN KEY (i_pessoas)        REFERENCES public.pessoas (i_pessoas) MATCH SIMPLE        ON UPDATE NO ACTION        ON DELETE NO ACTION        NOT VALID)WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.alugueis    OWNER to postgres;			-- Table: public.imoveis-- DROP TABLE public.imoveis;CREATE TABLE public.imoveis(    i_imoveis integer NOT NULL,    endereco character varying COLLATE pg_catalog."default" NOT NULL,    cidade character varying COLLATE pg_catalog."default" NOT NULL,    estado character varying COLLATE pg_catalog."default" NOT NULL,    CONSTRAINT i_imoveis PRIMARY KEY (i_imoveis))WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.imoveis    OWNER to postgres;				-- Table: public.pagamentos-- DROP TABLE public.pagamentos;CREATE TABLE public.pagamentos(    i_pagamentos integer NOT NULL,    parcelas integer NOT NULL,    valor numeric(15,2) NOT NULL,    tipo character varying COLLATE pg_catalog."default" NOT NULL,    CONSTRAINT i_pagamentos PRIMARY KEY (i_pagamentos))WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.pagamentos    OWNER to postgres;			-- Table: public.pessoas-- DROP TABLE public.pessoas;CREATE TABLE public.pessoas(    i_pessoas integer NOT NULL,    genero character varying COLLATE pg_catalog."default" NOT NULL,    cpf character varying COLLATE pg_catalog."default" NOT NULL,    endereco character varying COLLATE pg_catalog."default" NOT NULL,    nome character varying COLLATE pg_catalog."default" NOT NULL,    CONSTRAINT i_pessoas PRIMARY KEY (i_pessoas))WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.pessoas    OWNER to postgres;			-- Table: public.usuario-- DROP TABLE public.usuario;CREATE TABLE public.usuario(    id integer NOT NULL DEFAULT nextval('usuario_id_seq'::regclass),    nome character varying COLLATE pg_catalog."default",    email character varying COLLATE pg_catalog."default",    telefone character varying COLLATE pg_catalog."default",    senha character varying COLLATE pg_catalog."default")WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.usuario    OWNER to postgres;			-- Table: public.visitas-- DROP TABLE public.visitas;CREATE TABLE public.visitas(    i_visitas integer NOT NULL,    descricao character varying COLLATE pg_catalog."default" NOT NULL)WITH (    OIDS = FALSE)TABLESPACE pg_default;ALTER TABLE public.visitas    OWNER to postgres;				--select * from usuario;--update usuario set senha='admin' where nome='admin'--insert into usuario (nome, senha) values ('andre','andre'); --insert into usuario (nome, senha) values ('admin','admin'); 